import React,{Component} from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation.js'
import Logo from './components/Logo/Logo.js'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm.js'
import Rank from './components/Rank/Rank.js';
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import Signin from './components/Signin/Signin.js'
import Register from './components/Register/Register.js'
import axios from 'axios';

const app = new Clarifai.App({
 apiKey: '33459437c7c44126a88345158ebd1e27'
});


class  App extends Component {
  constructor(){
    super();
    this.state={
      input:'',
      imageUrl:'',
      box:{},
      route:'signin',
      selectedFile:null
    }
  }
  calculateFaceLocation=(data)=>{
   const clarifaiFace=data.outputs[0].data.regions[0].region_info.bounding_box;
   const image=document.getElementById('inputImage');
   const width=Number(image.width);
   const height=Number(image.height);
   return {
    leftCol:(clarifaiFace.left_col)*width,
    rightCol:width-(clarifaiFace.right_col)*width,
    topRow:(clarifaiFace.top_row)*height,
    bottomRow:height-(clarifaiFace.bottom_row)*height,
   }
  }
  displayFaceBox=(box)=>{
    this.setState({box:box});
  } 
  onInputChange=(event)=>{
    this.setState({input:event.target.value});
  }
  onSubmit=()=>{
    this.setState({imageUrl:this.state.input});
    app.models.predict({id:'d02b4508df58432fbb84e800597b8959'},this.state.input)
    .then(response=>this.displayFaceBox(this.calculateFaceLocation(response))).catch(err=>console.log(err));
      // we can also do .then for response and then .catch for error
  }
  onRouteChange=(page)=>{
    this.setState({route:page});
  }
  onFileChange=(event)=>{
    this.setState({selectedFile:event.target.files[0]});
    console.log(event.target.files[0]);
  }
  onFileUpload=()=>{
    let formData=new FormData();
    formData.append('myfile',this.state.selectedFile);
    formData.append('file_name',this.state.selectedFile.name);
    axios.post(" http://127.0.0.1:5000/uploadfile",formData);
  }
  render(){
    return (
      <div className="App">

        <Particles className='particle-canvas'
              params={{
                particles: {
                  number:{
                    value:100,
                    density:{
                      enable:true,
                      value_area:800
                    }
                  }
                }
              }}
            />

        {(this.state.route==='signin')?
          <Signin onRouteChange={this.onRouteChange}/>
        :(this.state.route==='home')?
          <div>
            <Navigation onRouteChange={this.onRouteChange}  />
            <Logo />
            <Rank />
            <ImageLinkForm onFileChange={this.onFileChange} onFileUpload={this.onFileUpload}/>
            // <FaceRecognition  className='center' imageUrl={this.state.imageUrl} Box={this.state.box}/>
          </div>
          :
          <Register onRouteChange={this.onRouteChange}/>
        }

      </div>
    );
  }
}
 /* (we are returning the sigin page if route is signin else
      the homepage will be returned.*/
export default App;
