import React,{Component} from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation.js'
import Logo from './components/Logo/Logo.js'
import Description_Upload from './components/Description_Upload/Description_Upload.js'
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import Analysis from './components/Analysis/Analysis.js'
import axios from 'axios';

class  App extends Component {
  constructor(){
    super();
    this.state={
      selectedFile:null,
      genres:null
    }
  }

  onFileChange=(event)=>{
    this.setState({selectedFile:event.target.files[0]});
    console.log(event.target.files[0]);
  }
  onFileUpload=()=>{
    if(this.selectedFile==null)return;

    let formData=new FormData();
    formData.append('myfile',this.state.selectedFile);
    formData.append('file_name',this.state.selectedFile.name);
    axios.post(" http://127.0.0.1:5000/uploadfile",formData)
    .then(res=>this.setState({genres:res.data.music_prediction}));
  }
  render(){
    return (
      <div className="App">
        <Particles className='particle-canvas'
              params={{
                particles: {
                  number:{
                    value:100,
                    density:{
                      enable:true,
                      value_area:800
                    }
                  }
                }
              }}
            />
          <div>
            <Navigation/>
            <Logo />

            <Description_Upload onFileChange={this.onFileChange} onFileUpload={this.onFileUpload}/>
            <Analysis genres={this.state.genres} />
          </div>
      </div>
    );
  }
}
 /* (we are returning the sigin page if route is signin else
      the homepage will be returned.*/
export default App;
